import React, {useState, useEffect } from "react";
import "./Account.css";

import Form from "react-bootstrap/Form";

function Account(props) {

    const [fName, setFName] = useState('');
    const [lName, setLName] = useState('');
    const [courses, setCourses] = useState('');

    let inputChangeHandler = (event) => {
        switch (event.target.name){
            case "fName":
                setFName(event.target.value)
                break;
            case "lName":
                setLName(event.target.value)
                break;
            case "courses":
                setCourses(event.target.value)
                break;
            default:
                console.log('input handler: nothing changed')
        }
    }


    let handleSubmit = async (event) => {
        event.preventDefault();


    }

    return (
        <div className="account-container">
            <Form onSubmit={handleSubmit}>
                <div><h3>User Account Info</h3></div>
                <Form.Group controlId="formBasicFname">
                    <Form.Label>First Name</Form.Label>
                    <Form.Control type="text" placeholder="Enter First Name" required name="fName" onChange={inputChangeHandler}/>
                </Form.Group>

                <Form.Group controlId="formBasicLname">
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control type="text" placeholder="Enter Last Name" required name="lName" onChange={inputChangeHandler}/>
                </Form.Group>

                <Form.Group controlId="formBasicUserName">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="text" placeholder="Enter a username" required name="username" onChange={inputChangeHandler}/>
                </Form.Group>
            </Form>
        </div>
    )
}

export default Account;
