import React,  { useState, useEffect } from "react";
import './Post.css';
import axios from "axios";
import config from "../config.json";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";

function Post(props) {

    const [postsData, setPostsData] = useState([]);

    useEffect(() => {
        async function fetchPosts() {
            try {
                const res = await axios.get(`${config.api.invokeURL}/posts`);
                setPostsData(res.data);
                console.log(res.data)
            }
            catch (err) {
                console.log("error fetching posts: ", err)
            }
        }
        fetchPosts();
    }, [])

    return (
        <div className='post-container'>
            <Button variant="primary" className="add-new-post-btn">Add new Post</Button>
            {postsData.map(post => {
                return (
                    <div className="post-container">
                        <Card style={{ width: '40rem', height: '30rem' }}>
                            <Card.Body>
                                <Card.Title>{post.postTitle}</Card.Title>
                                <Card.Subtitle className="mb-2 text-muted">by {post.postCreatorUsername}</Card.Subtitle>
                                <Card.Text>
                                    {post.postBody}
                                </Card.Text>
                                <div className="date-created">
                                    {post.postDateAndTimeCreated}
                                </div>
                            </Card.Body>
                        </Card>
                    </div>
                )
            })}
        </div>
    )
}

export default Post;
