import { useState, useEffect } from 'react';
import './Register.css';
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

import { Auth } from "aws-amplify";

function Register() {

    const [fName, setFName] = useState('');
    const [lName, setLName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    let inputChangeHandler = (event) => {
        switch (event.target.name){
            case "fName":
                setFName(event.target.value)
            case "lName":
                setLName(event.target.value)
            case "email":
                setEmail(event.target.value)
            case "password":
                setPassword(event.target.value)
        }
    }

    let handleSubmit = async (event) => {
        event.preventDefault();

        // AWS Cognito Work
        try {
            const signUpResponse = await Auth.signUp({
                username: email,
                password: password,
                attributes: email
            })
        }
        catch (err) {

        }
    }


    return (
        <div className="register-container">
            <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formBasicFname">
                    <Form.Label>First Name</Form.Label>
                    <Form.Control type="text" placeholder="Enter First Name" name="fName" onChange={inputChangeHandler}/>
                </Form.Group>

                <Form.Group controlId="formBasicLname">
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control type="text" placeholder="Enter Last Name" name="lName" onChange={inputChangeHandler}/>
                </Form.Group>

                <Form.Group controlId="formBasicEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email" placeholder="Enter email" name="email" onChange={inputChangeHandler}/>
                    <Form.Text className="text-muted">
                        We'll never share your email with anyone else.
                    </Form.Text>
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" name="password" onChange={inputChangeHandler}/>
                </Form.Group>
                <Form.Group controlId="formBasicCheckbox">
                    <Form.Check type="checkbox" label="I agree to all terms and conditions" />
                </Form.Group>
                <Button variant="primary" type="submit">
                    Register
                </Button>
            </Form>
        </div>
    )
}

export default Register;
